import React from 'react';
import Row from './Row';

class Board extends React.Component{
    constructor(props) {
      super(props)
    }
    componentDidMount() {
    }
    
     componentWillUnmount() {
    }

    drawGrid(){
        let rows = [];
        for(let i = 0; i < 8; i++){
            if(i % 2 == 1){
                rows.push(<Row color={true}/>);
            }
            else{
                rows.push(<Row/>);
            }
            
        }
        return rows;
    }
    
    render() {
       return(
          <div> 
           {this.drawGrid()}
         </div>
       );
     }
  }

  export default Board;
