import React from 'react';


class Tile extends React.Component{
    constructor(props){
      super(props); 
    }          
    componentDidMount() {
      const canvas = this.refs[this.props.boxid]
      const ctx = canvas.getContext("2d");
      ctx.moveTo(0,0);
      ctx.lineTo(50,0);
      ctx.lineTo(50,50);
      ctx.lineTo(0, 50);
      ctx.lineTo(0, 0);
      ctx.stroke();
    }
    
     componentWillUnmount() {
    }
                  
      render() {
       return( <canvas ref={this.props.boxid}width="50" height="50"></canvas>
         );
     }
    
  }

  export default Tile;
