import React from 'react';
import Board from './Board';

/*

Logic considerations

Initially creating the checker positions and passing them down to components

Deleting checkers once they're eliminated from the game

implementing possible moves for the checkers

checkers cannot occupy the same space

checkers must remain within the dimensions of the board

**/

class Game extends React.Component {
    constructor(props) {
      super(props);
      this.checkerpositions = this.initBoard();
      console.log(this.checkerpositions[0]);
      console.log(this.checkerpositions[1]);
      this.state = {player1: true, player1_checkers: this.checkerpositions[0], player2_checkers: this.checkerpositions[1]};
    this.playerMove = this.playerMove.bind(this);
    }

    initBoard(){
        let player1=[];
        let player2=[];
        let startingpositions = []
        for(let i = 0 ; i < 2; i++){
            for(let j = 0; j < 8; j++){
                player1.push([i+6,j]);
                player2.push([i, j]);
            }
        }
        startingpositions.push(player1);
        startingpositions.push(player2);
        return startingpositions;
    }

    playerMove(rowid, colid){
        alert("Player Move!");
        this.setState(state => ({
            player1: !state.player1
          }));
          alert(rowid, colid);
    }
    
    componentDidMount() {
    }
    
     componentWillUnmount() {
    }

    
     render() {
       return(
           <Board player1_checkers={this.state.player1_checkers} player2_checkers={this.state.player2_checkers} player1={this.state.player1} playermove={this.playerMove}/>
       );
     }
  }

  export default Game;
