import React from 'react';


class Tile extends React.Component{
    constructor(props){
      super(props);
      this.updateChecker = this.updateChecker.bind(this);
      this.flashChecker = this.flashChecker.bind(this);
      this.state = {flashcolor: false, activechecker: this.props.activechecker};
    }          

    updateChecker(){
      const canvas = this.refs[this.props.colid];
      const ctx = canvas.getContext("2d");
      const originalcolor = ctx.fillStyle;
      this.intervalFlash = setInterval(()=>{this.flashChecker(ctx, originalcolor)}, 1000);
      // alert(this.props.rowid, this.props.colid);
    }

    componentDidMount() {
      const canvas = this.refs[this.props.colid]
      const ctx = canvas.getContext("2d");
      ctx.moveTo(0,0);
      ctx.lineTo(50,0);
      ctx.lineTo(50,50);
      ctx.lineTo(0, 50);
      ctx.lineTo(0, 0);
      ctx.stroke();
      if(this.props.fill == true){
        ctx.fillRect(0,0,50,50);
      }
      if(this.props.checkercolor == "red"){
        if(this.state.activechecker[0] == this.props.rowid && this.state.activechecker[1] == this.props.colid){
          ctx.beginPath();
          ctx.arc(25, 25, 20, 0, 2*Math.PI);
          ctx.fillStyle = "#FF0000";
          ctx.fill()
          this.setState({flashing: this.flashChecker});
          setInterval(this.flashChecker(ctx, ctx.fillStyle), 1000)
        }
          ctx.beginPath();
          ctx.arc(25, 25, 20, 0, 2*Math.PI);
          ctx.fillStyle = "#FF0000";
          ctx.fill()
      }
      else if(this.props.checkercolor == "green"){
        if(this.state.activechecker[0] == this.props.rowid && this.state.activechecker[1] == this.props.colid){
          ctx.beginPath();
          ctx.arc(25, 25, 20, 0, 2*Math.PI);
          ctx.fillStyle = "#008000";
          ctx.fill()
          // this.setState({flashing: this.flashChecker});
          setInterval(this.flashChecker(ctx, ctx.fillStyle), 1000)
        }
        else{
          ctx.beginPath();
          ctx.arc(25, 25, 20, 0, 2*Math.PI);
          ctx.fillStyle = "#008000";
          ctx.fill()
        }
      }
    }

    flashChecker(ctx, originalcolor){
      this.setState({flashcolor: !(this.state.flashcolor)}, ()=>{
      ctx.beginPath();
      ctx.arc(25, 25, 20, 0, 2*Math.PI);
      if(this.state.flashcolor){
        ctx.fillStyle = "#0e11cc";
      }
      else{
        ctx.fillStyle = originalcolor;
      }
      
      ctx.fill()
      });
    }
    
     componentWillUnmount() {
    }             
      render() {

        //I'm pretty sure this decision making being done here violates the abstract state principle, this should be performed in component above
        if(this.props.checkercolor == "red" && this.props.player1 == true){
            return(<canvas ref={this.props.colid} width="50" onClick={(e) => this.props.playermove(this.props.rowid, this.props.colid, this.updateChecker, e)} height="50"></canvas>);
        }
        else if(this.props.checkercolor == "green" && this.props.player1 == false){
            return(<canvas ref={this.props.colid} width="50" onClick={(e) => this.props.playermove(this.props.rowid, this.props.colid, this.updateChecker, e)} height="50"></canvas>);
        }
        else{
            return(<canvas ref={this.props.colid} width="50" height="50"></canvas>);
        }
       
     }
    
  }

  export default Tile;
