import React from 'react';
import Tile from './Tile'

class Row extends React.Component {
    constructor(props) {
      super(props);
    }
    componentDidMount() {
    }

    componentWillUnmount() {
    }
    
    renderRow(){
      let tiles = []
      for(let i = 0; i < 8; i++){
          if(this.props.color == true){
            if(i%2 == 1){
                tiles.push(<Tile fill={true} rowid={this.rowid} colid={i}/>)
              }
              else{
                tiles.push(<Tile fill={false} rowid={this.rowid} colid={i}/>)
              }
          }
          else{
            if(i%2 == 1){
                tiles.push(<Tile fill={false} rowid={this.rowid} colid={i}/>)
              }
              else{
                tiles.push(<Tile fill={true} rowid={this.rowid} colid={i}/>)
            }
          
          }
      }
      return tiles;
    }
    
     render() {
       return(
          <div style={{height: '100px;'}}> 
           {this.renderRow()}
         </div>);
     }
  }

  export default Row;
